mapper.update:
UPDATE military_leave
SET leave_type = #{leaveType},
    leave_days = #{leaveDays},
    reason = #{reason}
WHERE leave_id = #{leaveId}

mapper.select:
SELECT * FROM military_leave
WHERE army_number = #{armyNumber}
ORDER BY reg_date_time DESC

SELECT * FROM military_leave
WHERE leave_id IN

    #{id}
